mapper.select:
SELECT COUNT(*)
FROM student
WHERE id = #{id}

SELECT s.*, g.name AS genearch_name, g.phone as genearch_phone
FROM student AS s
LEFT JOIN genearch AS g on s.refer_id = g.id

    1=1

        AND s.id = #{id}


        AND s.phone = #{phone}


        AND s.name LIKE concat('%',#{name},'%')


        AND s.sex = #{sex}


        AND s.grade = #{grade}


        AND s.genearch_id = #{genearchId}


        AND genearch_name = #{genearchName}


        AND genearch_phone = #{genearchPhone}


        AND genearch_relation = #{genearchRelation}


        AND s.enable = #{enable}


        AND s.refer_id = #{referId}



    ORDER BY

        ${key} ${value}
