mapper.select:
SELECT COUNT(*)
FROM student
WHERE id = #{id}

SELECT s.*, g.name AS genearch_name, g.phone as genearch_phone ,sg.relation AS genearch_relation,
GROUP_CONCAT(c.name SEPARATOR ' ') AS course_names
FROM student AS s
LEFT JOIN genearch AS g on s.refer_id = g.id
LEFT JOIN student_genearch sg on g.id = sg.genearch_id
LEFT JOIN student_course sc on s.id = sc.student_id
LEFT JOIN course c on sc.course_id = c.id

    1=1

        AND g.id = #{id}


        AND g.phone = #{phone}


        AND g.name LIKE concat('%',#{name},'%')


        AND g.sex = #{sex}


        AND g.grade = #{grade}


        AND g.genearch_id = #{genearchId}


        AND genearch_name = #{genearchName}


        AND genearch_phone = #{genearchPhone}


        AND genearch_relation = #{genearchRelation}


        AND g.enable = #{enable}


        AND g.refer_id = #{referId}


GROUP BY s.id

    ORDER BY

        ${key} ${value}
